    // 无法将writable从false改为true
    Object.defineProperty(person, 'type', {
        writable: true,
    });
    console.log(Object.getOwnPropertyDescriptor(person, 'type'));   // TypeError: Cannot redefine property: type


    // 无法重新配置enumerable，configurable属性
    Object.defineProperty(person, 'type', {
        enumerable: true,
    });
    console.log(Object.getOwnPropertyDescriptor(person, 'type'));   // TypeError: Cannot redefine property: type


